    // ====================================
    // CREATE ACTION - Process create form submission
    // ====================================

    /**
     * Process new agent form submission
     */
    protected function createAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(AgentVoter::CREATE);

        $agent = new Agent();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($agent);

        $form = $this->createForm(AgentType::class, $agent);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Re-set organization after form handling (form excludes this field)
            $organization = $this->tenantContext->getOrganizationForNewEntity();
            if ($organization) {
                $agent->setOrganization($organization);
                error_log('✅ AgentController: Organization re-set after form handling to ' . $organization->getName());
            }

            if ($form->isValid()) {
                try {
                    // Before create hook
                    $this->beforeCreate($agent);

                    $this->entityManager->persist($agent);
                    $this->entityManager->flush();

                    // After create hook
                    $this->afterCreate($agent);

                    $this->addFlash('success', $this->translator->trans(
                        'agent.flash.created_successfully',
                        ['%name%' => (string) $agent],
                        'agent'
                    ));

                    // If this is a modal/AJAX request (from "+" button), return Turbo Stream with event dispatch
                    // Check both GET and POST for modal parameter
                    if ($request->headers->get('X-Requested-With') === 'turbo-frame' ||
                        $request->get('modal') === '1') {

                        // Get display text for the entity
                        $displayText = (string) $agent;

                        $response = $this->render('_entity_created_success_stream.html.twig', [
                            'entityType' => 'Agent',
                            'entityId' => $agent->getId()->toRfc4122(),
                            'displayText' => $displayText,
                        ]);

                        // Set Turbo Stream content type so Turbo processes it without navigating
                        $response->headers->set('Content-Type', 'text/vnd.turbo-stream.html');

                        return $response;
                    }

                    return $this->redirectToRoute('agent_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'agent.flash.create_failed',
                        ['%error%' => $e->getMessage()],
                        'agent'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('agent/_form_modal.html.twig', [
            'form' => $form,
            'agent' => $agent,
            'is_edit' => false,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // EDIT FORM ACTION - Display edit form
    // ====================================

    /**
     * Display edit agent form
     */
    protected function editFormAction(Agent $agent, Request $request): Response
    {
        $this->denyAccessUnlessGranted(AgentVoter::EDIT, $agent);

        $form = $this->createForm(AgentType::class, $agent);

        return $this->render('agent/_form_modal.html.twig', [
            'form' => $form,
            'agent' => $agent,
            'is_edit' => true,
        ]);
    }

    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit agent form submission
     */
    protected function updateAction(Agent $agent, Request $request): Response
    {
        $this->denyAccessUnlessGranted(AgentVoter::EDIT, $agent);

        // Store original organization to preserve it
        $originalOrganization = $agent->getOrganization();

        $form = $this->createForm(AgentType::class, $agent);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $agent->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($agent);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($agent);

                    $this->addFlash('success', $this->translator->trans(
                        'agent.flash.updated_successfully',
                        ['%name%' => (string) $agent],
                        'agent'
                    ));

                    return $this->redirectToRoute('agent_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'agent.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'agent'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('agent/_form_modal.html.twig', [
            'form' => $form,
            'agent' => $agent,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete agent
    // ====================================

    /**
     * Delete agent
     */
    protected function deleteAction(Agent $agent, Request $request): Response
    {
        $this->denyAccessUnlessGranted(AgentVoter::DELETE, $agent);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_agent_' . $agent->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($agent);

            $name = (string) $agent;

            $this->entityManager->remove($agent);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'agent.flash.deleted_successfully',
                ['%name%' => $name],
                'agent'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'agent.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'agent'
            ));
        }

        return $this->redirectToRoute('agent_index', [], Response::HTTP_SEE_OTHER);
    }

    // ====================================
