    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit calendar form submission
     */
    protected function updateAction(Calendar $calendar, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CalendarVoter::EDIT, $calendar);

        // Store original organization to preserve it
        $originalOrganization = $calendar->getOrganization();

        $form = $this->createForm(CalendarType::class, $calendar);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $calendar->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($calendar);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($calendar);

                    $this->addFlash('success', $this->translator->trans(
                        'calendar.flash.updated_successfully',
                        ['%name%' => (string) $calendar],
                        'calendar'
                    ));

                    return $this->redirectToRoute('calendar_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'calendar.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'calendar'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('calendar/_form_modal.html.twig', [
            'form' => $form,
            'calendar' => $calendar,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete calendar
    // ====================================

    /**
     * Delete calendar
     */
    protected function deleteAction(Calendar $calendar, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CalendarVoter::DELETE, $calendar);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_calendar_' . $calendar->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($calendar);

            $name = (string) $calendar;

            $this->entityManager->remove($calendar);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'calendar.flash.deleted_successfully',
                ['%name%' => $name],
                'calendar'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'calendar.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'calendar'
            ));
        }

        return $this->redirectToRoute('calendar_index', [], Response::HTTP_SEE_OTHER);
    }

