    // ====================================
    // NEW FORM ACTION - Display create form
    // ====================================

    /**
     * Display new campaign form
     */
    protected function newFormAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(CampaignVoter::CREATE);

        $campaign = new Campaign();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($campaign);

        $form = $this->createForm(CampaignType::class, $campaign);

        return $this->render('campaign/_form_modal.html.twig', [
            'form' => $form,
            'campaign' => $campaign,
            'is_edit' => false,
        ]);
    }

    // ====================================
    // CREATE ACTION - Process create form submission
    // ====================================

    /**
     * Process new campaign form submission
     */
    protected function createAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(CampaignVoter::CREATE);

        $campaign = new Campaign();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($campaign);

        $form = $this->createForm(CampaignType::class, $campaign);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Re-set organization after form handling (form excludes this field)
            $organization = $this->tenantContext->getOrganizationForNewEntity();
            if ($organization) {
                $campaign->setOrganization($organization);
                error_log('✅ CampaignController: Organization re-set after form handling to ' . $organization->getName());
            }

            if ($form->isValid()) {
                try {
                    // Before create hook
                    $this->beforeCreate($campaign);

                    $this->entityManager->persist($campaign);
                    $this->entityManager->flush();

                    // After create hook
                    $this->afterCreate($campaign);

                    $this->addFlash('success', $this->translator->trans(
                        'campaign.flash.created_successfully',
                        ['%name%' => (string) $campaign],
                        'campaign'
                    ));

                    // If this is a modal/AJAX request (from "+" button), return Turbo Stream with event dispatch
                    // Check both GET and POST for modal parameter
                    if ($request->headers->get('X-Requested-With') === 'turbo-frame' ||
                        $request->get('modal') === '1') {

                        // Get display text for the entity
                        $displayText = (string) $campaign;

                        $response = $this->render('_entity_created_success_stream.html.twig', [
                            'entityType' => 'Campaign',
                            'entityId' => $campaign->getId()->toRfc4122(),
                            'displayText' => $displayText,
                        ]);

                        // Set Turbo Stream content type so Turbo processes it without navigating
                        $response->headers->set('Content-Type', 'text/vnd.turbo-stream.html');

                        return $response;
                    }

                    return $this->redirectToRoute('campaign_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'campaign.flash.create_failed',
                        ['%error%' => $e->getMessage()],
                        'campaign'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('campaign/_form_modal.html.twig', [
            'form' => $form,
            'campaign' => $campaign,
            'is_edit' => false,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // EDIT FORM ACTION - Display edit form
    // ====================================

    /**
     * Display edit campaign form
     */
    protected function editFormAction(Campaign $campaign, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CampaignVoter::EDIT, $campaign);

        $form = $this->createForm(CampaignType::class, $campaign);

        return $this->render('campaign/_form_modal.html.twig', [
            'form' => $form,
            'campaign' => $campaign,
            'is_edit' => true,
        ]);
    }

    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit campaign form submission
     */
    protected function updateAction(Campaign $campaign, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CampaignVoter::EDIT, $campaign);

        // Store original organization to preserve it
        $originalOrganization = $campaign->getOrganization();

        $form = $this->createForm(CampaignType::class, $campaign);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $campaign->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($campaign);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($campaign);

                    $this->addFlash('success', $this->translator->trans(
                        'campaign.flash.updated_successfully',
                        ['%name%' => (string) $campaign],
                        'campaign'
                    ));

                    return $this->redirectToRoute('campaign_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'campaign.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'campaign'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('campaign/_form_modal.html.twig', [
            'form' => $form,
            'campaign' => $campaign,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete campaign
    // ====================================

    /**
     * Delete campaign
     */
    protected function deleteAction(Campaign $campaign, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CampaignVoter::DELETE, $campaign);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_campaign_' . $campaign->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($campaign);

            $name = (string) $campaign;

            $this->entityManager->remove($campaign);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'campaign.flash.deleted_successfully',
                ['%name%' => $name],
                'campaign'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'campaign.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'campaign'
            ));
        }

        return $this->redirectToRoute('campaign_index', [], Response::HTTP_SEE_OTHER);
    }

