    /**
     * Delete city
     */
    protected function deleteAction(City $city, Request $request): Response
    {
        $this->denyAccessUnlessGranted(CityVoter::DELETE, $city);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_city_' . $city->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($city);

            $name = (string) $city;

            $this->entityManager->remove($city);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'city.flash.deleted_successfully',
                ['%name%' => $name],
                'city'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'city.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'city'
            ));
        }

        return $this->redirectToRoute('city_index', [], Response::HTTP_SEE_OTHER);
    }

