    // ====================================

    /**
     * Display new country form
     */
    protected function newFormAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(CountryVoter::CREATE);

        $country = new Country();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($country);

        $form = $this->createForm(CountryType::class, $country);

        return $this->render('country/_form_modal.html.twig', [
            'form' => $form,
            'country' => $country,
            'is_edit' => false,
        ]);
    }

    // ====================================
    // CREATE ACTION - Process create form submission
    // ====================================

    /**
     * Process new country form submission
     */
    protected function createAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(CountryVoter::CREATE);

        $country = new Country();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($country);

        $form = $this->createForm(CountryType::class, $country);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            if ($form->isValid()) {
                try {
                    // Before create hook
                    $this->beforeCreate($country);

                    $this->entityManager->persist($country);
                    $this->entityManager->flush();

                    // After create hook
                    $this->afterCreate($country);

                    $this->addFlash('success', $this->translator->trans(
                        'country.flash.created_successfully',
                        ['%name%' => (string) $country],
                        'country'
                    ));

                    // If this is a modal/AJAX request (from "+" button), return Turbo Stream with event dispatch
                    // Check both GET and POST for modal parameter
                    if ($request->headers->get('X-Requested-With') === 'turbo-frame' ||
                        $request->get('modal') === '1') {

                        // Get display text for the entity
                        $displayText = (string) $country;

                        $response = $this->render('_entity_created_success_stream.html.twig', [
                            'entityType' => 'Country',
                            'entityId' => $country->getId()->toRfc4122(),
                            'displayText' => $displayText,
                        ]);

                        // Set Turbo Stream content type so Turbo processes it without navigating
                        $response->headers->set('Content-Type', 'text/vnd.turbo-stream.html');

                        return $response;
                    }

                    return $this->redirectToRoute('country_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'country.flash.create_failed',
                        ['%error%' => $e->getMessage()],
                        'country'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('country/_form_modal.html.twig', [
            'form' => $form,
            'country' => $country,
            'is_edit' => false,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

