    // ====================================
    // NEW FORM ACTION - Display create form
    // ====================================

    /**
     * Display new event form
     */
    protected function newFormAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(EventVoter::CREATE);

        $event = new Event();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($event);

        $form = $this->createForm(EventType::class, $event);

        return $this->render('event/_form_modal.html.twig', [
            'form' => $form,
            'event' => $event,
            'is_edit' => false,
        ]);
    }

    // ====================================
    // CREATE ACTION - Process create form submission
    // ====================================

    /**
     * Process new event form submission
     */
    protected function createAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(EventVoter::CREATE);

        $event = new Event();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($event);

        $form = $this->createForm(EventType::class, $event);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Re-set organization after form handling (form excludes this field)
            $organization = $this->tenantContext->getOrganizationForNewEntity();
            if ($organization) {
                $event->setOrganization($organization);
                error_log('✅ EventController: Organization re-set after form handling to ' . $organization->getName());
            }

            if ($form->isValid()) {
                try {
                    // Before create hook
                    $this->beforeCreate($event);

                    $this->entityManager->persist($event);
                    $this->entityManager->flush();

                    // After create hook
                    $this->afterCreate($event);

                    $this->addFlash('success', $this->translator->trans(
                        'event.flash.created_successfully',
                        ['%name%' => (string) $event],
                        'event'
                    ));

                    // If this is a modal/AJAX request (from "+" button), return Turbo Stream with event dispatch
                    // Check both GET and POST for modal parameter
                    if ($request->headers->get('X-Requested-With') === 'turbo-frame' ||
                        $request->get('modal') === '1') {

                        // Get display text for the entity
                        $displayText = (string) $event;

                        $response = $this->render('_entity_created_success_stream.html.twig', [
                            'entityType' => 'Event',
                            'entityId' => $event->getId()->toRfc4122(),
                            'displayText' => $displayText,
                        ]);

                        // Set Turbo Stream content type so Turbo processes it without navigating
                        $response->headers->set('Content-Type', 'text/vnd.turbo-stream.html');

                        return $response;
                    }

                    return $this->redirectToRoute('event_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'event.flash.create_failed',
                        ['%error%' => $e->getMessage()],
                        'event'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('event/_form_modal.html.twig', [
            'form' => $form,
            'event' => $event,
            'is_edit' => false,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // EDIT FORM ACTION - Display edit form
    // ====================================

    /**
     * Display edit event form
     */
    protected function editFormAction(Event $event, Request $request): Response
    {
        $this->denyAccessUnlessGranted(EventVoter::EDIT, $event);

        $form = $this->createForm(EventType::class, $event);

        return $this->render('event/_form_modal.html.twig', [
            'form' => $form,
            'event' => $event,
            'is_edit' => true,
        ]);
    }

    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit event form submission
     */
    protected function updateAction(Event $event, Request $request): Response
    {
        $this->denyAccessUnlessGranted(EventVoter::EDIT, $event);

        // Store original organization to preserve it
        $originalOrganization = $event->getOrganization();

        $form = $this->createForm(EventType::class, $event);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $event->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($event);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($event);

                    $this->addFlash('success', $this->translator->trans(
                        'event.flash.updated_successfully',
                        ['%name%' => (string) $event],
                        'event'
                    ));

                    return $this->redirectToRoute('event_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'event.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'event'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('event/_form_modal.html.twig', [
            'form' => $form,
            'event' => $event,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete event
    // ====================================

    /**
     * Delete event
     */
    protected function deleteAction(Event $event, Request $request): Response
    {
        $this->denyAccessUnlessGranted(EventVoter::DELETE, $event);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_event_' . $event->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($event);

            $name = (string) $event;

            $this->entityManager->remove($event);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'event.flash.deleted_successfully',
                ['%name%' => $name],
                'event'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'event.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'event'
            ));
        }

        return $this->redirectToRoute('event_index', [], Response::HTTP_SEE_OTHER);
    }

