    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit holiday form submission
     */
    protected function updateAction(Holiday $holiday, Request $request): Response
    {
        $this->denyAccessUnlessGranted(HolidayVoter::EDIT, $holiday);

        // Store original organization to preserve it
        $originalOrganization = $holiday->getOrganization();

        $form = $this->createForm(HolidayType::class, $holiday);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $holiday->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($holiday);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($holiday);

                    $this->addFlash('success', $this->translator->trans(
                        'holiday.flash.updated_successfully',
                        ['%name%' => (string) $holiday],
                        'holiday'
                    ));

                    return $this->redirectToRoute('holiday_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'holiday.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'holiday'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('holiday/_form_modal.html.twig', [
            'form' => $form,
            'holiday' => $holiday,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete holiday
    // ====================================

    /**
     * Delete holiday
     */
    protected function deleteAction(Holiday $holiday, Request $request): Response
    {
        $this->denyAccessUnlessGranted(HolidayVoter::DELETE, $holiday);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_holiday_' . $holiday->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($holiday);

            $name = (string) $holiday;

            $this->entityManager->remove($holiday);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'holiday.flash.deleted_successfully',
                ['%name%' => $name],
                'holiday'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'holiday.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'holiday'
            ));
        }

        return $this->redirectToRoute('holiday_index', [], Response::HTTP_SEE_OTHER);
    }

