    // ====================================
    // API SEARCH - Fetch Products for list view
    // ====================================

    /**
     * API endpoint for searching Products
     */
    protected function apiSearchAction(Request $request): JsonResponse
    {
        $this->denyAccessUnlessGranted(ProductVoter::LIST);

        // Delegate to parent BaseApiController which handles
        // search, filtering, sorting, and pagination
        return parent::apiSearchAction($request);
    }

    // ====================================
    // NEW FORM ACTION - Display create form
    // ====================================

    /**
     * Display new product form
     */
    protected function newFormAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::CREATE);

        $product = new Product();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($product);

        $form = $this->createForm(ProductType::class, $product);

        return $this->render('product/_form_modal.html.twig', [
            'form' => $form,
            'product' => $product,
            'is_edit' => false,
        ]);
    }

    // ====================================
    // CREATE ACTION - Process create form submission
    // ====================================

    /**
     * Process new product form submission
     */
    protected function createAction(Request $request): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::CREATE);

        $product = new Product();

        // Initialize with custom logic if needed
        $this->initializeNewEntity($product);

        $form = $this->createForm(ProductType::class, $product);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Re-set organization after form handling (form excludes this field)
            $organization = $this->tenantContext->getOrganizationForNewEntity();
            if ($organization) {
                $product->setOrganization($organization);
                error_log('✅ ProductController: Organization re-set after form handling to ' . $organization->getName());
            }

            if ($form->isValid()) {
                try {
                    // Before create hook
                    $this->beforeCreate($product);

                    $this->entityManager->persist($product);
                    $this->entityManager->flush();

                    // After create hook
                    $this->afterCreate($product);

                    $this->addFlash('success', $this->translator->trans(
                        'product.flash.created_successfully',
                        ['%name%' => (string) $product],
                        'product'
                    ));

                    // If this is a modal/AJAX request (from "+" button), return Turbo Stream with event dispatch
                    // Check both GET and POST for modal parameter
                    if ($request->headers->get('X-Requested-With') === 'turbo-frame' ||
                        $request->get('modal') === '1') {

                        // Get display text for the entity
                        $displayText = (string) $product;

                        $response = $this->render('_entity_created_success_stream.html.twig', [
                            'entityType' => 'Product',
                            'entityId' => $product->getId()->toRfc4122(),
                            'displayText' => $displayText,
                        ]);

                        // Set Turbo Stream content type so Turbo processes it without navigating
                        $response->headers->set('Content-Type', 'text/vnd.turbo-stream.html');

                        return $response;
                    }

                    return $this->redirectToRoute('product_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'product.flash.create_failed',
                        ['%error%' => $e->getMessage()],
                        'product'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('product/_form_modal.html.twig', [
            'form' => $form,
            'product' => $product,
            'is_edit' => false,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // EDIT FORM ACTION - Display edit form
    // ====================================

    /**
     * Display edit product form
     */
    protected function editFormAction(Product $product, Request $request): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::EDIT, $product);

        $form = $this->createForm(ProductType::class, $product);

        return $this->render('product/_form_modal.html.twig', [
            'form' => $form,
            'product' => $product,
            'is_edit' => true,
        ]);
    }

    // ====================================
    // UPDATE ACTION - Process edit form submission
    // ====================================

    /**
     * Process edit product form submission
     */
    protected function updateAction(Product $product, Request $request): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::EDIT, $product);

        // Store original organization to preserve it
        $originalOrganization = $product->getOrganization();

        $form = $this->createForm(ProductType::class, $product);
        $form->handleRequest($request);

        if ($form->isSubmitted()) {
            // Restore organization after form handling (form excludes this field)
            if ($originalOrganization) {
                $product->setOrganization($originalOrganization);
            }

            if ($form->isValid()) {
                try {
                    // Before update hook
                    $this->beforeUpdate($product);

                    $this->entityManager->flush();

                    // After update hook
                    $this->afterUpdate($product);

                    $this->addFlash('success', $this->translator->trans(
                        'product.flash.updated_successfully',
                        ['%name%' => (string) $product],
                        'product'
                    ));

                    return $this->redirectToRoute('product_index', [], Response::HTTP_SEE_OTHER);

                } catch (\Exception $e) {
                    $this->addFlash('error', $this->translator->trans(
                        'product.flash.update_failed',
                        ['%error%' => $e->getMessage()],
                        'product'
                    ));
                }
            }
        }

        // Re-render with errors
        return $this->render('product/_form_modal.html.twig', [
            'form' => $form,
            'product' => $product,
            'is_edit' => true,
        ], new Response('', $form->isSubmitted() ? Response::HTTP_UNPROCESSABLE_ENTITY : Response::HTTP_OK));
    }

    // ====================================
    // DELETE ACTION - Delete product
    // ====================================

    /**
     * Delete product
     */
    protected function deleteAction(Product $product, Request $request): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::DELETE, $product);

        // Verify CSRF token
        $token = $request->request->get('_token');
        if (!$this->csrfTokenManager->isTokenValid(new CsrfToken('delete_product_' . $product->getId()->toString(), $token))) {
            throw new InvalidCsrfTokenException('Invalid CSRF token for delete operation.');
        }

        try {
            // Before delete hook
            $this->beforeDelete($product);

            $name = (string) $product;

            $this->entityManager->remove($product);
            $this->entityManager->flush();

            // After delete hook
            $this->afterDelete();

            $this->addFlash('success', $this->translator->trans(
                'product.flash.deleted_successfully',
                ['%name%' => $name],
                'product'
            ));

        } catch (\Exception $e) {
            $this->addFlash('error', $this->translator->trans(
                'product.flash.delete_failed',
                ['%error%' => $e->getMessage()],
                'product'
            ));
        }

        return $this->redirectToRoute('product_index', [], Response::HTTP_SEE_OTHER);
    }

    // ====================================
    // SHOW ACTION - Display product details
    // ====================================

    /**
     * Display product details
     */
    protected function showAction(Product $product): Response
    {
        $this->denyAccessUnlessGranted(ProductVoter::VIEW, $product);

        // Build show properties configuration for view
        $showProperties = $this->buildShowProperties($product);

        return $this->render('product/show.html.twig', [
            'product' => $product,
            'showProperties' => $showProperties,
        ]);
    }

    /**
     * Build show properties configuration
     * Override this method in ProductController to customize displayed properties
     */
    protected function buildShowProperties(Product $product): array
    {
        return [];
    }

